INSERT INTO `app_customer` VALUES ('9', '中国邮政', 'pictures/customer/2018/05/17/customer4_1.png', '中国邮政', '<p><span style=\"color: rgb(153, 153, 153); font-family: &quot;Open Sans&quot;, Arial, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, 微软雅黑, STHeiti, &quot;WenQuanYi Micro Hei&quot;, SimSun, sans-serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\">中国邮政集团公司是依照《中华人民共和国全民所有制工业企业法》组建的大型国有独资企业，依法经营各项邮政业务，承担邮政普遍服务义务，受政府委托提供邮政特殊服务，对竞争性邮政业务实行商业化运营。</span></p>', '2', '1', '2018-05-17 14:16:50.498000', '2018-05-17 14:16:50.498000', '4');
